import React, { useState } from "react";
import "../css/card.css";

export default function Card({ number }) {
  const [isFlipped, setIsFlipped] = useState(false);
  const cardFront = "Welcome to GFG.";
  const cardBack = "Computer Science Portal.";
  const handleFlip = () => {
    setIsFlipped(!isFlipped);
  };

  return (
    <div>
      <div className={`flip-card ${isFlipped ? "flipped" : ""}`}>
        <div onClick={handleFlip} className="flip-card-inner">
          <div className="flip-card-front">
            <div className="card-content">{cardFront}</div>
          </div>
          <div onClick={handleFlip} className="flip-card-back">
            <div className="card-content">{cardBack}</div>
          </div>
        </div>
      </div>
    </div>
  );
}
