import React, { useState } from "react";
import "../css/card.css";

export default function Card({ number }) {
  const [title, setTitle] = useState("Eric");
  const [isToggled, setIsToggled] = useState(true);

  const handleChange = () => {
    setIsToggled(!isToggled);
    changeTitle();
  };

  const changeTitle = () => {
    isToggled ? setTitle("Jenn") : setTitle("Eric");
  };

  return (
    <div>
      <div onClick={handleChange} className="card-container">
        {number}
      </div>

      <h1>{title}</h1>
    </div>
  );
}
