import React, { useState } from "react";
import "../css/card.css";

export default function Card({ number }) {
  const [isfliped, setIsFliped] = useState(false);

  return (
    <div>
      <div
        onClick={() => setIsFliped(!isfliped)}
        className={`front-card-container ${isfliped ? "flip" : ""}`}
      >
        {number}
      </div>
    </div>
  );
}
